nggridverticalspacing&gt;&lt;w:displayhorizontaldrawinggridevery&gt;0&lt;/w:displayhorizontaldrawinggridevery&gt;&lt;w:displayverticaldrawinggridevery&gt;2&lt;/w:displayverticaldrawinggridevery&gt;&lt;w:validateagainstschemas /&gt;&lt;w:saveifxmlinvalid&gt;false&lt;/w:saveifxmlinvalid&gt;&lt;w:ignoremixedcontent&gt;false&lt;/w:ignoremixedcontent&gt;&lt;w:alwaysshowplaceholdertext&gt;false&lt;/w:alwaysshowplaceholdertext&gt;&lt;w:compatibility&gt;&lt;w:spaceforul /&gt;&lt;w:balancesinglebytedoublebytewidth /&gt;&lt;w:donotleavebackslashalone /&gt;&lt;w:ultrailspace /&gt;&lt;w:donotexpandshiftreturn /&gt;&lt;w:adjustlineheightintable /&gt;&lt;w:breakwrappedtables /&gt;&lt;w:snaptogridincell /&gt;&lt;w:wraptextwithpunct /&gt;&lt;w:useasianbreakrules /&gt;&lt;w:dontgrowautofit /&gt;&lt;w:usefelayout /&gt;&lt;/w:compatibility&gt;&lt;w:browserlevel&gt;MicrosoftInternetExplorer4&lt;/w:browserlevel&gt;&lt;/w:worddocument&gt;&lt;/xml&gt;&lt;![endif]--&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom:6.0pt;text-indent:21.0pt;mso-char-indent-count:2.0;&quot;&gt;&lt;span lang=&quot;EN-US&quot;&gt;2014&lt;/span&gt;年&lt;span lang=&quot;EN-US&quot;&gt;9&lt;/span&gt;月&lt;span lang=&quot;EN-US&quot;&gt;16&lt;/span&gt;日上午，由中国茶叶流通协会、中国茶叶学会、中华全国供销合作总社杭州茶叶研究院主办，福鼎市人民政府承办的福鼎白茶陈宗懋院士工作站签约授牌暨第二届福鼎白茶斗茶赛获奖茶封存仪式隆重举行！&lt;img src=&quot;/attached/20140916/20140916165435_5609.jpg&quot; title=&quot;福鼎斗茶赛白茶茶艺表演&quot; alt=&quot;福鼎斗茶赛白茶茶艺表演&quot; border=&quot;0&quot;/&gt; &amp;nbsp; &lt;br/&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom:6.0pt;text-indent:21.0pt;mso-char-indent-count:2.0;&quot;&gt;品品香白茶茶艺表演&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom:6.0pt;text-indent:21.0pt;mso-char-indent-count:2.0;&quot;&gt;&lt;!--[if gte mso 9]&gt;&lt;xml&gt;&lt;w:worddocument&gt;&lt;w:view&gt;Normal&lt;/w:view&gt;&lt;w:zoom&gt;0&lt;/w:zoom&gt;&lt;w:punctuationkerning /&gt;&lt;w:drawinggridverticalspacing&gt;7.8 磅&lt;/w:drawinggridverticalspacing&gt;&lt;w:displayhorizontaldrawinggridevery&gt;0&lt;/w:displayhorizontaldrawinggridevery&gt;&lt;w:displayverticaldrawinggridevery&gt;2&lt;/w:displayverticaldrawinggridevery&gt;&lt;w:validateagainstschemas /&gt;&lt;w:saveifxmlinvalid&gt;false&lt;/w:saveifxmlinvalid&gt;&lt;w:ignoremixedcontent&gt;false&lt;/w:ignoremixedcontent&gt;&lt;w:alwaysshowplaceholdertext&gt;false&lt;/w:alwaysshowplaceholdertext&gt;&lt;w:compatibility&gt;&lt;w:spaceforul /&gt;&lt;w:balancesinglebytedoublebytewidth /&gt;&lt;w:donotleavebackslashalone /&gt;&lt;w:ultrailspace /&gt;&lt;w:donotexpandshiftreturn /&gt;&lt;w:adjustlineheightintable /&gt;&lt;w:breakwrappedtables /&gt;&lt;w:snaptogridincell /&gt;&lt;w:wraptextwithpunct /&gt;&lt;w:useasianbreakrules /&gt;&lt;w:dontgrowautofit /&gt;&lt;w:usefelayout /&gt;&lt;/w:compatibility&gt;&lt;w:browserlevel&gt;MicrosoftInternetExplorer4&lt;/w:browserlevel&gt;&lt;/w:worddocument&gt;&lt;/xml&gt;&lt;![endif]--&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom:6.0pt;text-indent:21.0pt;mso-char-indent-count:2.0;&quot;&gt;&lt;!--[if gte mso 9]&gt;&lt;xml&gt;&lt;w:latentstyles deflockedstate=&quot;false&quot; latentstylecount=&quot;156&quot;&gt;&lt;/w:latentstyles&gt;&lt;/xml&gt;&lt;![endif]--&gt;&lt;!--[if gte mso 10]&gt;&lt;style&gt;/* Style Definitions */\r\n table.MsoNormalTable\r\n	{mso-style-name:普通表格;\r\n	mso-tstyle-rowband-size:0;\r\n	mso-tstyle-colband-size:0;\r\n	mso-style-noshow:yes;\r\n	mso-style-parent:&quot;&quot;;\r\n	mso-padding-alt:0cm 5.4pt 0cm 5.4pt;\r\n	mso-para-margin:0cm;\r\n	mso-para-margin-bottom:.0001pt;\r\n	mso-pagination:widow-orphan;\r\n	font-size:10.0pt;\r\n	font-family:&quot;Times New Roman&quot;;\r\n	mso-fareast-font-family:&quot;Times New Roman&quot;;\r\n	mso-ansi-language:#0400;\r\n	mso-fareast-language:#0400;\r\n	mso-bidi-language:#0400;}&lt;/style&gt;&lt;![endif]--&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom:6.0pt;text-indent:18.0pt;mso-char-indent-count:2.0;line-height:normal;&quot;&gt;&lt;!--[if gte mso 9]&gt;&lt;xml&gt;&lt;w:worddocument&gt;&lt;w:view&gt;Normal&lt;/w:view&gt;&lt;w:zoom&gt;0&lt;/w:zoom&gt;&lt;w:punctuationkerning /&gt;&lt;w:drawinggridverticalspacing&gt;7.8 磅&lt;/w:drawinggridverticalspacing&gt;&lt;w:displayhorizontaldrawinggridevery&gt;0&lt;/w:displayhorizontaldrawinggridevery&gt;&lt;w:displayverticaldrawinggridevery&gt;2&lt;/w:displayverticaldrawinggridevery&gt;&lt;w:validateagainstschemas /&gt;&lt;w:saveifxmlinvalid&gt;false&lt;/w:saveifxmlinvalid&gt;&lt;w:ignoremixedcontent&gt;false&lt;/w:ignoremixedcontent&gt;&lt;w:alwaysshowplaceholdertext&gt;false&lt;/w:alwaysshowplaceholdertext&gt;&lt;w:compatibility&gt;&lt;w:spaceforul /&gt;&lt;w:balancesinglebytedoublebytewidth /&gt;&lt;w:donotleavebackslashalone /&gt;&lt;w:ultrailspace /&gt;&lt;w:donotexpandshiftreturn /&gt;&lt;w:adjustlineheightintable /&gt;&lt;w:breakwrappedtables /&gt;&lt;w:snaptogridincell /&gt;&lt;w:wraptextwithpunct /&gt;&lt;w:useasianbreakrules /&gt;&lt;w:dontgrowautofit /&gt;&lt;w:usefelayout /&gt;&lt;/w:compatibility&gt;&lt;w:browserlevel&gt;MicrosoftInternetExplorer4&lt;/w:browserlevel&gt;&lt;/w:worddocument&gt;&lt;/xml&gt;&lt;![endif]--&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom:6.0pt;text-indent:18.0pt;mso-char-indent-count:2.0;line-height:normal;&quot;&gt;&lt;!--[if gte mso 9]&gt;&lt;xml&gt;&lt;w:latentstyles deflockedstate=&quot;false&quot; latentstylecount=&quot;156&quot;&gt;&lt;/w:latentstyles&gt;&lt;/xml&gt;&lt;![endif]--&gt;&lt;!--[if gte mso 10]&gt;&lt;style&gt;/* Style Definitions */\r\n table.MsoNormalTable\r\n	{mso-style-name:普通表格;\r\n	mso-tstyle-rowband-size:0;\r\n	mso-tstyle-colband-size:0;\r\n	mso-style-noshow:yes;\r\n	mso-style-parent:&quot;&quot;;\r\n	mso-padding-alt:0cm 5.4pt 0cm 5.4pt;\r\n	mso-para-margin:0cm;\r\n	mso-para-margin-bottom:.0001pt;\r\n	mso-pagination:widow-orphan;\r\n	font-size:10.0pt;\r\n	font-family:&quot;Times New Roman&quot;;\r\n	mso-fareast-font-family:&quot;Times New Roman&quot;;\r\n	mso-ansi-language:#0400;\r\n	mso-fareast-language:#0400;\r\n	mso-bidi-language:#0400;}&lt;/style&gt;&lt;![endif]--&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom:6.0pt;text-indent:21.0pt;mso-char-indent-count: 2.0&quot;&gt;中国工程院院士陈宗懋、中国茶叶学会理事长江用文、福建农林大安溪茶学院副院长孙威江、中国茶叶科学研究所科技处处长林智、原福建省茶叶公司副总经理陈金水、省农业厅种植业处教授级高级农艺师苏峰、宁德市人大副主任蓝兴贵、原宁德市委副书记唐颐、宁德市海峡茶业交流协会会长李步泉、宁德市茶业局局长郑云锡、福鼎市人大常委会主任陈兴华、福鼎市人大常委会副主任郭如盾、福鼎市政府副市长陈辉、福鼎市政协副主席吴祖霖、福鼎市政府副调研员张德志等专家领导，及各界茶人和媒体朋友&lt;span lang=&quot;EN-US&quot;&gt;300&lt;/span&gt;多人参与了本次盛会。&lt;span style=&quot;mso-bidi-font-size:10.5pt;&quot;&gt;品品香作为本次盛会两个状元奖项的获得公司，由林健董事长带领公司代表应邀参加了本次封存仪式。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;mso-bidi-font-size:10.5pt;&quot;&gt;&lt;img src=&quot;/attached/20140916/20140916165508_9673.jpg&quot; title=&quot;福鼎白茶斗茶赛陈宗懋院士演讲&quot; alt=&quot;福鼎白茶斗茶赛陈宗懋院士演讲&quot; border=&quot;0&quot;/&gt;&amp;nbsp;&amp;nbsp; &lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;mso-bidi-font-size:10.5pt;&quot;&gt; 陈宗懋院士演讲&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;mso-bidi-font-size:10.5pt;&quot;&gt;&lt;img src=&quot;/attached/20140916/20140916165545_4051.jpg&quot; title=&quot;福鼎白茶斗茶赛授牌仪式&quot; alt=&quot;福鼎白茶斗茶赛授牌仪式&quot; border=&quot;0&quot;/&gt; &amp;nbsp; &lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;mso-bidi-font-size:10.5pt;&quot;&gt; 授牌仪式&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;mso-bidi-font-size:10.5pt;&quot;&gt;&lt;img src=&quot;/attached/20140916/20140916165603_0302.jpg&quot; title=&quot;品品香林健董事长演讲&quot; alt=&quot;品品香林健董事长演讲&quot; border=&quot;0&quot;/&gt; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;mso-bidi-font-size:10.5pt;&quot;&gt;林健董事长演讲&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;mso-bidi-font-size:10.5pt;&quot;&gt;&lt;img src=&quot;/attached/20140916/20140916165620_7490.jpg&quot; title=&quot;品品香林健董事长接受采访&quot; alt=&quot;品品香林健董事长接受采访&quot; border=&quot;0&quot;/&gt; &amp;nbsp; &lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;mso-bidi-font-size:10.5pt;&quot;&gt; 林健董事长接受采访&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom:6.0pt;text-indent:18.0pt;mso-char-indent-count:2.0;line-height:normal;&quot;&gt;&lt;!--[if gte mso 9]&gt;&lt;xml&gt;&lt;w:worddocument&gt;&lt;w:view&gt;Normal&lt;/w:view&gt;&lt;w:zoom&gt;0&lt;/w:zoom&gt;&lt;w:punctuationkerning /&gt;&lt;w:drawinggridverticalspacing&gt;7.8 磅&lt;/w:drawinggridverticalspacing&gt;&lt;w:displayhorizontaldrawinggridevery&gt;0&lt;/w:displayhorizontaldrawinggridevery&gt;&lt;w:displayverticaldrawinggridevery&gt;2&lt;/w:displayverticaldrawinggridevery&gt;&lt;w:validateagainstschemas /&gt;&lt;w:saveifxmlinvalid&gt;false&lt;/w:saveifxmlinvalid&gt;&lt;w:ignoremixedcontent&gt;false&lt;/w:ignoremixedcontent&gt;&lt;w:alwaysshowplaceholdertext&gt;false&lt;/w:alwaysshowplaceholdertext&gt;&lt;w:compatibility&gt;&lt;w:spaceforul /&gt;&lt;w:balancesinglebytedoublebytewidth /&gt;&lt;w:donotleavebackslashalone /&gt;&lt;w:ultrailspace /&gt;&lt;w:donotexpandshiftreturn /&gt;&lt;w:adjustlineheightintable /&gt;&lt;w:breakwrappedtables /&gt;&lt;w:snaptogridincell /&gt;&lt;w:wraptextwithpunct /&gt;&lt;w:useasianbreakrules /&gt;&lt;w:dontgrowautofit /&gt;&lt;w:usefelayout /&gt;&lt;/w:compatibility&gt;&lt;w:browserlevel&gt;MicrosoftInternetExplorer4&lt;/w:browserlevel&gt;&lt;/w:worddocument&gt;&lt;/xml&gt;&lt;![endif]--&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom:6.0pt;text-indent:18.0pt;mso-char-indent-count:2.0;line-height:normal;&quot;&gt;&lt;!--[if gte mso 9]&gt;&lt;xml&gt;&lt;w:latentstyles deflockedstate=&quot;false&quot; latentstylecount=&quot;156&quot;&gt;&lt;/w:latentstyles&gt;&lt;/xml&gt;&lt;![endif]--&gt;&lt;!--[if gte mso 10]&gt;&lt;style&gt;/* Style Definitions */\r\n table.MsoNormalTable\r\n	{mso-style-name:普通表格;\r\n	mso-tstyle-rowband-size:0;\r\n	mso-tstyle-colband-size:0;\r\n	mso-style-noshow:yes;\r\n	mso-style-parent:&quot;&quot;;\r\n	mso-padding-alt:0cm 5.4pt 0cm 5.4pt;\r\n	mso-para-margin:0cm;\r\n	mso-para-margin-bottom:.0001pt;\r\n	mso-pagination:widow-orphan;\r\n	font-size:10.0pt;\r\n	font-family:&quot;Times New Roman&quot;;\r\n	mso-fareast-font-family:&quot;Times New Roman&quot;;\r\n	mso-ansi-language:#0400;\r\n	mso-fareast-language:#0400;\r\n	mso-bidi-language:#0400;}&lt;/style&gt;&lt;![endif]--&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom:6.0pt;text-indent:21.0pt;mso-char-indent-count:2.0;&quot;&gt;&lt;span style=&quot;mso-bidi-font-size:10.5pt;&quot;&gt;盛会现场，来自品品香公司的茶艺师们以美轮美奂的茶艺表演，为盛会拉开了序幕。在活动中几位主要专家、领导先后发表了重要讲话，品品香公司林健董事长作为杰出企业代表也发表了演讲。活动共进行了&lt;/span&gt;福鼎白茶陈宗懋院士工作站签约授牌和第二届福鼎白茶斗茶赛获奖茶封存仪式两项议程&lt;span style=&quot;mso-bidi-font-size:10.5pt;&quot;&gt;，&lt;/span&gt;福鼎白茶陈宗懋院士工作站的正式授牌值得所有福鼎白茶事业同仁们为之兴奋，而&lt;span style=&quot;mso-bidi-font-size:10.5pt;&quot;&gt;品品香公司在颁奖仪式中，荣获福鼎白茶斗茶赛所设三个状元奖项中的两个状元，则令在场所有人感到叹服。最后，由几位主要专家、领导亲自在包括品品香白毫银针、白牡丹等茶样在内的斗茶赛状元茶样上签字封存，宣告了盛会的结束。&lt;img src=&quot;/attached/20140916/20140916165639_0304.jpg&quot; title=&quot;颁奖仪式，品品香荣获两项状元荣誉&quot; alt=&quot;颁奖仪式，品品香荣获两项状元荣誉&quot; border=&quot;0&quot;/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom:6.0pt;text-indent:21.0pt;mso-char-indent-count:2.0;&quot;&gt;&lt;span style=&quot;mso-bidi-font-size:10.5pt;&quot;&gt;颁奖仪式，品品香荣获两项状元荣誉&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom:6.0pt;text-indent:21.0pt;mso-char-indent-count:2.0;&quot;&gt;&lt;span style=&quot;mso-bidi-font-size:10.5pt;&quot;&gt;&lt;img src=&quot;/attached/20140916/20140916165652_8743.jpg&quot; title=&quot;封存仪式，陈宗懋院士等专家、领导为品品香白毫银针、白牡丹等状元茶签字&quot; alt=&quot;封存仪式，陈宗懋院士等专家、领导为品品香白毫银针、白牡丹等状元茶签字&quot; border=&quot;0&quot;/&gt; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom:6.0pt;text-indent:21.0pt;mso-char-indent-count:2.0;&quot;&gt;&lt;span style=&quot;mso-bidi-font-size:10.5pt;&quot;&gt;封存仪式，陈宗懋院士等专家、领导为品品香白毫银针、白牡丹等状元茶签字&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom:6.0pt;text-indent:21.0pt;mso-char-indent-count:2.0;&quot;&gt;&lt;!--[if gte mso 9]&gt;&lt;xml&gt;&lt;w:worddocument&gt;&lt;w:view&gt;Normal&lt;/w:view&gt;&lt;w:zoom&gt;0&lt;/w:zoom&gt;&lt;w:punctuationkerning /&gt;&lt;w:drawinggridverticalspacing&gt;7.8 磅&lt;/w:drawinggridverticalspacing&gt;&lt;w:displayhorizontaldrawinggridevery&gt;0&lt;/w:displayhorizontaldrawinggridevery&gt;&lt;w:displayverticaldrawinggridevery&gt;2&lt;/w:displayverticaldrawinggridevery&gt;&lt;w:validateagainstschemas /&gt;&lt;w:saveifxmlinvalid&gt;false&lt;/w:saveifxmlinvalid&gt;&lt;w:ignoremixedcontent&gt;false&lt;/w:ignoremixedcontent&gt;&lt;w:alwaysshowplaceholdertext&gt;false&lt;/w:alwaysshowplaceholdertext&gt;&lt;w:compatibility&gt;&lt;w:spaceforul /&gt;&lt;w:balancesinglebytedoublebytewidth /&gt;&lt;w:donotleavebackslashalone /&gt;&lt;w:ultrailspace /&gt;&lt;w:donotexpandshiftreturn /&gt;&lt;w:adjustlineheightintable /&gt;&lt;w:breakwrappedtables /&gt;&lt;w:snaptogridincell /&gt;&lt;w:wraptextwithpunct /&gt;&lt;w:useasianbreakrules /&gt;&lt;w:dontgrowautofit /&gt;&lt;w:usefelayout /&gt;&lt;/w:compatibility&gt;&lt;w:browserlevel&gt;MicrosoftInternetExplorer4&lt;/w:browserlevel&gt;&lt;/w:worddocument&gt;&lt;/xml&gt;&lt;![endif]--&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom:6.0pt;text-indent:21.0pt;mso-char-indent-count:2.0;&quot;&gt;&lt;!--[if gte mso 9]&gt;&lt;xml&gt;&lt;w:latentstyles deflockedstate=&quot;false&quot; latentstylecount=&quot;156&quot;&gt;&lt;/w:latentstyles&gt;&lt;/xml&gt;&lt;![endif]--&gt;&lt;!--[if gte mso 10]&gt;&lt;style&gt;/* Style Definitions */\r\n table.MsoNormalTable\r\n	{mso-style-name:普通表格;\r\n	mso-tstyle-rowband-size:0;\r\n	mso-tstyle-colband-size:0;\r\n	mso-style-noshow:yes;\r\n	mso-style-parent:&quot;&quot;;\r\n	mso-padding-alt:0cm 5.4pt 0cm 5.4pt;\r\n	mso-para-margin:0cm;\r\n	mso-para-margin-bottom:.0001pt;\r\n	mso-pagination:widow-orphan;\r\n	font-size:10.0pt;\r\n	font-family:&quot;Times New Roman&quot;;\r\n	mso-fareast-font-family:&quot;Times New Roman&quot;;\r\n	mso-ansi-language:#0400;\r\n	mso-fareast-language:#0400;\r\n	mso-bidi-language:#0400;}&lt;/style&gt;&lt;![endif]--&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom:7.8pt;text-indent:21.0pt;mso-char-indent-count:2.0;&quot;&gt;&lt;!--[if gte mso 9]&gt;&lt;xml&gt;&lt;w:worddocument&gt;&lt;w:view&gt;Normal&lt;/w:view&gt;&lt;w:zoom&gt;0&lt;/w:zoom&gt;&lt;w:punctuationkerning /&gt;&lt;w:drawinggridverticalspacing&gt;7.8 磅&lt;/w:drawinggridverticalspacing&gt;&lt;w:displayhorizontaldrawinggridevery&gt;0&lt;/w:displayhorizontaldrawinggridevery&gt;&lt;w:displayverticaldrawinggridevery&gt;2&lt;/w:displayverticaldrawinggridevery&gt;&lt;w:validateagainstschemas /&gt;&lt;w:saveifxmlinvalid&gt;false&lt;/w:saveifxmlinvalid&gt;&lt;w:ignoremixedcontent&gt;false&lt;/w:ignoremixedcontent&gt;&lt;w:alwaysshowplaceholdertext&gt;false&lt;/w:alwaysshowplaceholdertext&gt;&lt;w:compatibility&gt;&lt;w:spaceforul /&gt;&lt;w:balancesinglebytedoublebytewidth /&gt;&lt;w:donotleavebackslashalone /&gt;&lt;w:ultrailspace /&gt;&lt;w:donotexpandshiftreturn /&gt;&lt;w:adjustlineheightintable /&gt;&lt;w:breakwrappedtables /&gt;&lt;w:snaptogridincell /&gt;&lt;w:wraptextwithpunct /&gt;&lt;w:useasianbreakrules /&gt;&lt;w:dontgrowautofit /&gt;&lt;w:usefelayout /&gt;&lt;/w:compatibility&gt;&lt;w:browserlevel&gt;MicrosoftInternetExplorer4&lt;/w:browserlevel&gt;&lt;/w:worddocument&gt;&lt;/xml&gt;&lt;![endif]--&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom:7.8pt;text-indent:21.0pt;mso-char-indent-count:2.0;&quot;&gt;&lt;!--[if gte mso 9]&gt;&lt;xml&gt;&lt;w:latentstyles deflockedstate=&quot;false&quot; latentstylecount=&quot;156&quot;&gt;&lt;/w:latentstyles&gt;&lt;/xml&gt;&lt;![endif]--&gt;&lt;!--[if gte mso 10]&gt;&lt;style&gt;/* Style Definitions */\r\n table.MsoNormalTable\r\n	{mso-style-name:普通表格;\r\n	mso-tstyle-rowband-size:0;\r\n	mso-tstyle-colband-size:0;\r\n	mso-style-noshow:yes;\r\n	mso-style-parent:&quot;&quot;;\r\n	mso-padding-alt:0cm 5.4pt 0cm 5.4pt;\r\n	mso-para-margin:0cm;\r\n	mso-para-margin-bottom:.0001pt;\r\n	mso-pagination:widow-orphan;\r\n	font-size:10.0pt;\r\n	font-family:&quot;Times New Roman&quot;;\r\n	mso-fareast-font-family:&quot;Times New Roman&quot;;\r\n	mso-ansi-language:#0400;\r\n	mso-fareast-language:#0400;\r\n	mso-bidi-language:#0400;}&lt;/style&gt;&lt;![endif]--&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormalIndent&quot; style=&quot;margin-bottom:6.0pt;&quot;&gt;&lt;span style=&quot;mso-font-kerning:0pt;&quot;&gt;活动结束，品品香再度满载荣誉而归，在&lt;span lang=&quot;EN-US&quot;&gt;2013&lt;/span&gt;年首届福鼎白茶斗茶赛上，品品香同样以荣获赛事所设五个状元奖项中的三项状元，而令业界瞩目。之所以能取得如此成就，除了得到来自各级协会和政府领导、各大科研院校、专家的支持和关心，也如品品香林健董事长在本届封存仪式上所说，这也是品品香二十余载淡定执着努力的成果。二十余载，品品香始终以一杯自然、健康、品位的好茶献给千家万户，始终以&lt;span lang=&quot;EN-US&quot;&gt;“&lt;/span&gt;淡定、执着、合作、共赢&lt;span lang=&quot;EN-US&quot;&gt;”&lt;/span&gt;的企业精神，以&lt;span lang=&quot;EN-US&quot;&gt;“&lt;/span&gt;创新技术，创建品牌，创意文化&lt;span lang=&quot;EN-US&quot;&gt;”&lt;/span&gt;的企业理念，全力打造中国白茶第一品牌，只为做大做强茶产业，真正实现兴茶惠民的美好愿景。品品香将以一个又一个的成绩，证明品品香人的坚守。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;br/&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom:6.0pt;text-indent:18.0pt;mso-char-indent-count:2.0;line-height:normal;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom:6.0pt;text-indent:21.0pt;mso-char-indent-count:2.0;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom:6.0pt;text-indent:21.0pt;mso-char-indent-count:2.0;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;!--[if gte mso 9]&gt;&lt;xml&gt;&lt;w:latentstyles deflockedstate=&quot;false&quot; latentstylecount=&quot;156&quot;&gt;&lt;/w:latentstyles&gt;&lt;/xml&gt;&lt;![endif]--&gt;&lt;!--[if !mso]&gt;&lt;object classid=&quot;clsid:38481807-CA0E-42D2-BF39-B33AF135CC4D&quot; id=&quot;ieooui&quot;&gt;&lt;/object&gt;&lt;style&gt;st1\\:*{behavior:url(#ieooui) }&lt;/style&gt;&lt;![endif]--&gt;&lt;!--[if gte mso 10]&gt;&lt;style&gt;/* Style Definitions */\r\n table.MsoNormalTable\r\n	{mso-style-name:普通表格;\r\n	mso-tstyle-rowband-size:0;\r\n	mso-tstyle-colband-size:0;\r\n	mso-style-noshow:yes;\r\n	mso-style-parent:&quot;&quot;;\r\n	mso-padding-alt:0cm 5.4pt 0cm 5.4pt;\r\n	mso-para-margin:0cm;\r\n	mso-para-margin-bottom:.0001pt;\r\n	mso-pagination:widow-orphan;\r\n	font-size:10.0pt;\r\n	font-family:&quot;Times New Roman&quot;;\r\n	mso-fareast-font-family:&quot;Times New Roman&quot;;\r\n	mso-ansi-language:#0400;\r\n	mso-fareast-language:#0400;\r\n	mso-bidi-language:#0400;}&lt;/style&gt;&lt;![endif]--&gt;&lt;!--[if gte mso 9]&gt;&lt;xml&gt;&lt;w:latentstyles deflockedstate=&quot;false&quot; latentstylecount=&quot;156&quot;&gt;&lt;/w:latentstyles&gt;&lt;/xml&gt;&lt;![endif]--&gt;&lt;!--[if gte mso 10]&gt;&lt;style&gt;/* Style Definitions */\r\n table.MsoNormalTable\r\n	{mso-style-name:普通表格;\r\n	mso-tstyle-rowband-size:0;\r\n	mso-tstyle-colband-size:0;\r\n	mso-style-noshow:yes;\r\n	mso-style-parent:&quot;&quot;;\r\n	mso-padding-alt:0cm 5.4pt 0cm 5.4pt;\r\n	mso-para-margin:0cm;\r\n	mso-para-margin-bottom:.0001pt;\r\n	mso-pagination:widow-orphan;\r\n	font-size:10.0pt;\r\n	font-family:&quot;Times New Roman&quot;;\r\n	mso-fareast-font-family:&quot;Times New Roman&quot;;\r\n	mso-ansi-language:#0400;\r\n	mso-fareast-language:#0400;\r\n	mso-bidi-language:#0400;}&lt;/style&gt;&lt;![endif]--&gt;','/attached/20140916/20140916165435_5609.jpg'),(1944,12,'&lt;!--[if gte mso 9]&gt;&lt;xml&gt;&lt;w:worddocument&gt;&lt;w:browserlevel&gt;MicrosoftInternetExplorer4&lt;/w:browserlevel&gt;&lt;w:displayhorizontaldrawinggridevery&gt;0&lt;/w:displayhorizontaldrawinggridevery&gt;&lt;w:displayverticaldrawinggridevery&gt;2&lt;/w:displayverticaldrawinggridevery&gt;&lt;w:documentkind&gt;DocumentNotSpecified&lt;/w:documentkind&gt;&lt;w:drawinggridverticalspacing&gt;7.8 磅&lt;/w:drawinggridverticalspacing&gt;&lt;w:punctuationkerning&gt;&lt;/w:punctuationkerning&gt;&lt;w:view&gt;Normal&lt;/w:view&gt;&lt;w:compatibility&gt;&lt;w:dontgrowautofit /&gt;&lt;w:balancesinglebytedoublebytewidth /&gt;&lt;w:donotexpandshiftreturn /&gt;&lt;w:usefelayout /&gt;&lt;/w:compatibility&gt;&lt;w:zoom&gt;0&lt;/w:zoom&gt;&lt;/w:worddocument&gt;&lt;/xml&gt;&lt;![endif]--&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;text-indent:21.0000pt;mso-char-indent-count:2.0000;&quot;&gt;&lt;span style=&quot;mso-spacerun:&amp;#39;yes&amp;#39;;font-family:宋体;font-size:10.5000pt;mso-font-kerning:1.0000pt;&quot;&gt;2015&lt;span style=&quot;font-family:宋体;&quot;&gt;年&lt;/span&gt;&lt;span style=&quot;font-family:&amp;#39;Times New Roman&amp;#39;;&quot;&gt;5&lt;/span&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;月&lt;/span&gt;&lt;span style=&quot;font-family:&amp;#39;Times New Roman&amp;#39;;&quot;&gt;15&lt;/span&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;日上午，第八届东莞国际茶业博览会正式开幕，中国白茶领导品牌品品香为推广健康白茶，弘扬白茶文化，积极参与了本次展会。&lt;img src=&quot;/attached/20150515/20150515164503_0201.jpg&quot; title=&quot;陈兴华主任讲话&quot; alt=&quot;陈兴华主任讲话&quot; height=&quot;426&quot; border=&quot;0&quot; width=&quot;567&quot;/&gt; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;text-indent:21.0000pt;mso-char-indent-count:2.0000;&quot;&gt;&lt;span style=&quot;mso-spacerun:&amp;#39;yes&amp;#39;;font-family:宋体;font-size:10.5000pt;mso-font-kerning:1.0000pt;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;陈兴华主任讲话&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;text-indent:21.0000pt;mso-char-indent-count:2.0000;&quot;&gt;&lt;span style=&quot;mso-spacerun:&amp;#39;yes&amp;#39;;font-family:宋体;font-size:10.5000pt;mso-font-kerning:1.0000pt;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;&lt;img src=&quot;/attached/20150515/20150515164516_5829.jpg&quot; title=&quot;刘仲华教授科学论证白茶健康&quot; alt=&quot;刘仲华教授科学论证白茶健康&quot; border=&quot;0&quot;/&gt; &amp;nbsp; &lt;br/&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;text-indent:21.0000pt;mso-char-indent-count:2.0000;&quot;&gt;&lt;span style=&quot;mso-spacerun:&amp;#39;yes&amp;#39;;font-family:宋体;font-size:10.5000pt;mso-font-kerning:1.0000pt;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt; 刘仲华教授科学论证白茶健康&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;text-indent:21.0000pt;mso-char-indent-count:2.0000;&quot;&gt;&lt;span style=&quot;mso-spacerun:&amp;#39;yes&amp;#39;;font-family:宋体;font-size:10.5000pt;mso-font-kerning:1.0000pt;&quot;&gt;5&lt;span style=&quot;font-family:宋体;&quot;&gt;月&lt;/span&gt;&lt;span style=&quot;font-family:&amp;#39;Times New Roman&amp;#39;;&quot;&gt;15&lt;/span&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;日上午，展会现场还举办了一场“相约东莞&amp;nbsp;共享健康”福鼎白茶专场推介会，活动由福鼎市茶业发展领导小组主办，福鼎市茶业局、福鼎市茶业协会承办，品品香与多家福鼎白茶企业共同协办。会上，福鼎市人大主任陈兴华为到场嘉宾介绍了白茶，湖南农业大学教授刘仲华则为嘉宾们分析健康白茶的科学依据。作为积极参与活动的协办方之一，品品香公司董事长林健也上台发表福鼎白茶的专题解析，结合品品香公司二十余年的努力之路，为与会朋友们展现了一个正在崛起的茶类，一个正在不断建设完善的品牌。&lt;img src=&quot;/attached/20150515/20150515164531_6769.jpg&quot; title=&quot;林健董事长解读白茶、品品香&quot; alt=&quot;林健董事长解读白茶、品品香&quot; border=&quot;0&quot;/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;br/&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;text-indent:21.0000pt;mso-char-indent-count:2.0000;&quot;&gt;&lt;span style=&quot;mso-spacerun:&amp;#39;yes&amp;#39;;font-family:宋体;font-size:10.5000pt;mso-font-kerning:1.0000pt;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;林健董事长解读白茶、品品香&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;text-indent:21.0000pt;mso-char-indent-count:2.0000;&quot;&gt;&lt;span style=&quot;mso-spacerun:&amp;#39;yes&amp;#39;;font-family:宋体;font-size:10.5000pt;mso-font-kerning:1.0000pt;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;&lt;img src=&quot;/attached/20150515/20150515164543_9115.jpg&quot; title=&quot;畲族民间艺术家表演“木偶泡茶”&quot; alt=&quot;畲族民间艺术家表演“木偶泡茶”&quot; border=&quot;0&quot;/&gt; &amp;nbsp;&amp;nbsp; &lt;br/&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;text-indent:21.0000pt;mso-char-indent-count:2.0000;&quot;&gt;&lt;span style=&quot;mso-spacerun:&amp;#39;yes&amp;#39;;font-family:宋体;font-size:10.5000pt;mso-font-kerning:1.0000pt;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;畲族民间艺术家表演“木偶泡茶”&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;text-indent:21.0000pt;mso-char-indent-count:2.0000;&quot;&gt;&lt;span style=&quot;mso-spacerun:&amp;#39;yes&amp;#39;;font-family:宋体;font-size:10.5000pt;mso-font-kerning:1.0000pt;&quot;&gt;除了该场活动中，品品香赢得茶友们瞩目外，在本次展会现场，品品香展位也迎来了各方茶友，自然、健康、品位的好茶受到了茶友们的欢迎，品品香传扬健康白茶的理念，也得到了许多茶友的认可。本场展会将开办至&lt;span style=&quot;font-family:&amp;#39;Times New Roman&amp;#39;;&quot;&gt;5&lt;/span&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;月&lt;/span&gt;&lt;span style=&quot;font-family:&amp;#39;Times New Roman&amp;#39;;&quot;&gt;18&lt;/span&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt;日，品品香将继续在展会现场守候每位茶友的到来。&lt;img src=&quot;/attached/20150515/20150515164554_9586.jpg&quot; title=&quot;品品香展位现场&quot; alt=&quot;品品香展位现场&quot; border=&quot;0&quot;/&gt;&amp;nbsp;&amp;nbsp; &lt;br/&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;text-indent:21.0000pt;mso-char-indent-count:2.0000;&quot;&gt;&lt;span style=&quot;mso-spacerun:&amp;#39;yes&amp;#39;;font-family:宋体;font-size:10.5000pt;mso-font-kerning:1.0000pt;&quot;&gt;&lt;span style=&quot;font-family:宋体;&quot;&gt; 品品香展位现场&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;!--[if gte mso 9]&gt;&lt;xml&gt;&lt;w:latentstyles deflockedstate=&quot;false&quot; defunhidewhenused=&quot;true&quot; defsemihidden=&quot;true&quot; defqformat=&quot;false&quot; defpriority=&quot;99&quot; latentstylecount=&quot;260&quot;&gt;&lt;w:lsdexception locked=&quot;false&quot; priority=&quot;99&quot; semihidden=&quot;false&quot; name=&quot;Normal&quot;&gt;&lt;/w:lsdexception&gt;&lt;w:lsdexception locked=&quot;false&quot; priority=&quot;99&quot; semihidden=&quot;false&quot; name=&quot;heading 1&quot;&gt;&lt;/w:lsdexception&gt;&lt;w:lsdexception locked=&quot;false&quot; priority=&quot;99&quot; semihidden=&quot;false&quot; name=&quot;heading 2&quot;&gt;&lt;/w:lsdexception&gt;&lt;w:lsdexception locked=&quot;false&quot; priority=&quot;99&quot; semihidden=&quot;false&quot; name=&quot;heading 3&quot;&gt;&lt;/w:lsdexception&gt;&lt;w:lsdexception locked=&quot;false&quot; priority=&quot;99&quot; semihidden=&quot;false&quot; name=&quot;heading 4&quot;&gt;&lt;/w:lsdexception&gt;&lt;w:lsdexception locked=&quot;false&quot; priority=&quot;99&quot; semihidden=&quot;false&quot; name=&quot;heading 5&quot;&gt;&lt;/w:lsdexception&gt;&lt;w:lsdexception locked=&quot;false&quot; priority=&quot;99&quot; semihidden=&quot;false&quot; name=&quot;heading 6&quot;&gt;&lt;/w:lsdexception&gt;&lt;w:lsdexception locked=&quot;false&quot; priority=&quot;99&quot; semihidden=&quot;false&quot; name=&quot;heading 7&quot;&gt;&lt;/w:lsdexception&gt;&lt;w:lsdexception locked=&quot;false&quot; priority=&quot;99&quot; semihidden=&quot;false&quot; name=&quot;heading 8&quot;&gt;&lt;/w:lsdexception&gt;&lt;w:lsdexception locked=&quot;false&quot; priority=&quot;99&quot; semihidden=&quot;false&quot; name=&quot;heading 9&quot;&gt;&lt;/w:lsdexception&gt;&lt;w:lsdexception locked=&quot;false&quot; priority=&quot;99&quot; semihidden=&quot;false&quot; name=&quot;index 1&quot;&gt;&lt;/w:lsdexception&gt;&lt;w:lsdexception locked=&quot;false&quot; priority=&quot;99&quot; semihidden=&quot;false&quot; name=&quot;index 2&quot;&gt;&lt;/w:lsdexception&gt;&lt;w:lsdexception locked=&quot;false&quot; priority=&quot;99&quot; semihidden=&quot;false&quot; name=&quot;index 3&quot;&gt;&lt;/w:lsdexception&gt;&lt;w:lsdexception locked=&quot;false&quot; priority=&quot;99&quot; semihidden=&quot;false&quot; name=&quot;index 4